// Import the Subcomponent component from the specified folder
import Subcomponent from "./subcomponents/SubExample";

// Define the Ecosystem component as a functional component
const Ecosystem = () => {

        const [modal, setModal] = useState({
            open: false, // Indicates if the modal is open
            imgSrc: "", // Source of the image to display in the modal
            description: "", // Description of the image
          });
        
          // Function to open the modal and set the image and description
          const openModal = (imgSrc, description) => {
            setModal({ open: true, imgSrc, description });
          };
        
          // Function to close the modal
          const closeModal = () => {
            setModal({ open: false, imgSrc: "", description: "" });
          };
        
          // Return the JSX for rendering the WoodlandConservationSite component
          return (
            <div className="h-screen flex flex-col justify-center items-center px-6">
              {/* Title Section */}
              <div className="text-center mb-8">
                {/* Main Heading */}
                <h1 className="text-4xl font-bold mb-2 text-gray-800">
                  Woodland Conservation Site
                </h1>
                {/* Subheading */}
                <h2 className="text-2xl italic text-green-700">
                  EcoSystem: A Living Testament to Nature and History
                </h2>
              </div>
        
              {/* Content Section */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-8 w-full max-w-6xl">
                {/* Left Content */}
                <div>
                  {/* Paragraph describing the woodland site */}
                  <p className="text-justify text-gray-700 mb-6">
                    This woodland conservation site is home to an extraordinary array
                    of natural and historical treasures. The towering Yellow Birch trees
                    provide shade and shelter for countless species, playing a key role
                    in the forest's ecosystem. The site also includes wetlands, a
                    critical habitat that supports biodiversity, regulates water flow,
                    and filters pollutants. The wetland area is a serene reminder of
                    natureâ€™s resilience and its importance to the environment. Together,
                    these features make the site a haven for both wildlife and
                    eco-conscious visitors.
                  </p>
                  {/* Image with onClick handler to open the modal */}
                  <div
                    className="relative cursor-pointer"
                    onClick={() =>
                      openModal(
                        "https://via.placeholder.com/800x600", // Replace with actual image URL
                        "This is a beautiful woodland area featuring Yellow Birch trees and a serene wetland."
                      )
                    }
                  >
                    <img
                      src="https://via.placeholder.com/400x300" // Replace with actual image URL
                      alt="Woodland"
                      className="rounded-lg"
                    />
                    {/* Button text overlay */}
                    <button className="absolute bottom-2 left-2 bg-black text-white px-2 py-1 text-sm rounded">
                      Click on image for more details
                    </button>
                  </div>
                </div>
        
                {/* Right Content */}
                <div>
                  {/* Image with onClick handler to open the modal */}
                  <div
                    className="relative cursor-pointer mb-6"
                    onClick={() =>
                      openModal(
                        "https://via.placeholder.com/800x600", // Replace with actual image URL
                        "This is a historical site showcasing ancient farmhouse artifacts and wells."
                      )
                    }
                  >
                    <img
                      src="https://via.placeholder.com/400x300" // Replace with actual image URL
                      alt="EcoSystem"
                      className="rounded-lg"
                    />
                    {/* Button text overlay */}
                    <button className="absolute bottom-2 left-2 bg-black text-white px-2 py-1 text-sm rounded">
                      Click on image for more details
                    </button>
                  </div>
                  {/* Paragraph describing the historical significance */}
                  <p className="text-justify text-gray-700">
                    Beyond its natural beauty, this conservation site also holds
                    historical significance. Evidence of a farmhouse and ancient wells
                    provides a glimpse into the lives of those who lived here
                    generations ago. These artifacts are a testament to the human
                    connection with the land and serve as a reminder of the importance
                    of preserving history alongside nature. Visitors can explore these
                    relics, marveling at how the site weaves together past and present
                    in a truly unique way. Whether drawn by the Yellow Birch, wetlands,
                    or historical artifacts, there is something here for everyone.
                  </p>
                </div>
              </div>
        
              {/* Modal Section */}
              {modal.open && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
                  {/* Modal container */}
                  <div className="bg-white rounded-lg overflow-hidden shadow-lg max-w-2xl">
                    {/* Modal image */}
                    <img src={modal.imgSrc} alt="Modal Content" className="w-full" />
                    {/* Modal description */}
                    <div className="p-4">
                      <p className="text-gray-700">{modal.description}</p>
                      {/* Close button */}
                      <button
                        onClick={closeModal}
                        className="mt-4 bg-green-700 text-white px-4 py-2 rounded"
                      >
                        Close
                      </button>
                    </div>
                  </div>
                </div>
              )}
            </div>
    );
}

// Export the Ecosystem component so it can be used in other parts of the app
export default Ecosystem;
