import React, { useState } from "react";

function ImageCard(props) {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleImageClick = () => {
    setIsModalOpen(true); // Open the modal when the image is clicked
  };

  const handleCloseModal = () => {
    setIsModalOpen(false); // Close the modal when clicking outside or pressing a close button
  };

  return (
    <div className="relative flex-row space-y-4 bg-white smshadow rounded-xl my-2">
      {/* Main Image Container */}
      <div
        className="h-52 overflow-hidden rounded-xl transition-transform duration-300
                    custom-scale hover:cursor-pointer hover:border-white hover:border-2"
        onClick={handleImageClick} // Open modal on click
      >
        <img
          className="w-full h-full object-cover object-[20%_40%] rounded-md"
          src={props.url}
          alt="img"
        />
      </div>

      {/* Title and Description */}
      <div className="hidden">
        <h3 className="text-left text-2xl mx-3">{props.title}</h3>
        <p className="text-justify mx-3">{props.description}</p>
        <audio className="px-2 my-2 w-full" controls></audio>
      </div>

      {/* Modal */}
      {isModalOpen && (
        <div
          className="fixed top-0 left-0 w-full h-full bg-pink-500 flex items-center justify-center z-50"
          onClick={handleCloseModal} // Close modal on clicking outside
        >
          <div
            className="bg-pink-500 w-full h-full flex items-center justify-center"
            onClick={(e) => e.stopPropagation()} // Prevent closing when clicking inside the modal
          >
            <p className="text-white text-2xl font-bold">This is the pink div!</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default ImageCard;
