import React, { useState } from "react";
import Subcomponent from "./subcomponents/SubExample"; // Import Subcomponent

const Contact = () => {
    const [darkMode, setDarkMode] = useState(false);

    // Toggle Light/Dark Mode
    const toggleDarkMode = () => {
        setDarkMode(!darkMode);
    };

    // Text-to-Speech Function
    const playAudio = (text) => {
        const speech = new SpeechSynthesisUtterance(text);
        speech.voice = speechSynthesis.getVoices().find(
            (voice) => voice.lang === "en-US" && voice.name.includes("Google")
        );
        speech.rate = 1;
        speech.pitch = 1;
        speech.volume = 1;
        speechSynthesis.speak(speech);
    };

    return (
        <div className={`contact-page ${darkMode ? "dark-mode" : "light-mode"}`}>
            {/* Menu Bar */}
            <header className="menu-bar">
                <h1 className="site-title">Woodland Conservation</h1>
                <nav>
                    <a href="/about">About</a>
                    <a href="/gallery">Gallery</a>
                    <a href="/ecosystem">Eco-System</a>
                    <a href="/contact" className="active">
                        Contact
                    </a>
                </nav>
                <button onClick={toggleDarkMode} className="toggle-button">
                    {darkMode ? "Switch to Light Mode" : "Switch to Dark Mode"}
                </button>
            </header>


                {/* Form Section */}
                <div className="form-section">
                    <form className="contact-form">
                        <div className="form-row">
                            <label>
                                Full Name:
                                <input
                                    type="text"
                                    name="fullName"
                                    placeholder="Enter your name"
                                    required
                                />
                            </label>
                            <label>
                                Email ID:
                                <input
                                    type="email"
                                    name="email"
                                    placeholder="someone@gmail.com"
                                    required
                                />
                            </label>
                        </div>
                        <div className="form-row">
                            <label>
                                Phone Number:
                                <input
                                    type="tel"
                                    name="phone"
                                    placeholder="(xxx)-xxx-xxxx"
                                />
                            </label>
                            <label>
                                Message:
                                <textarea
                                    name="message"
                                    placeholder="Your thoughts..."
                                    required
                                ></textarea>
                            </label>
                        </div>
                        <button type="submit" className="submit-button">
                            Send
                        </button>
                    </form>
                </div>

                {/* Text-to-Speech Example */}
                <div className="text-to-speech">
                    <button
                        onClick={() =>
                            playAudio(
                                "This is the contact section of Woodland Conservation. Fill out the form to get in touch."
                            )
                        }
                    >
                        Play Audio
                    </button>
                </div>

                {/* Subcomponent */}
                <Subcomponent />
        
        </div>
    );
};

export default Contact;
