import React, { useState, useEffect } from "react";

function ImageCard(props) {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [imageClass, setImageClass] = useState("h-full"); // Default to `h-full`
  const [divClass, setDivClass] = useState("flex-row"); // Default to `flex`

  const handleImageClick = () => {
    setIsModalOpen(true); // Open the modal when the image is clicked
  };

  const handleCloseModal = () => {
    setIsModalOpen(false); // Close the modal when clicking outside or pressing a close button
  };

  useEffect(() => {
    // Disable scrolling when the modal is open
    if (isModalOpen) {
      document.body.style.overflow = "hidden";
    } else {
      document.body.style.overflow = "auto"; // Enable scrolling when the modal is closed
    }

    // Cleanup function to ensure scrolling is restored
    return () => {
      document.body.style.overflow = "auto";
    };
  }, [isModalOpen]);

  useEffect(() => {
    // Dynamically check dimensions of the image
    const imgElement = document.querySelector(".big-img");
    if (imgElement) {
      if (imgElement.complete) {
        uppdateClasses(imgElement);
      } else {
        imgElement.onload = () => uppdateClasses(imgElement);
      }
    }
  }, [isModalOpen]);

  const uppdateClasses = (imgElement) => {
    if (imgElement.naturalWidth > imgElement.naturalHeight) {
      setImageClass("w-full");
      setDivClass("flex-row");
    } else {
      setImageClass("h-full");
      setDivClass("flex");
    }
  };

  return (
    <div className="relative flex-row space-y-4 bg-white smshadow rounded-xl my-2">
      {/* Main Image Container */}
      <div
        className="h-52 overflow-hidden rounded-xl transition-transform duration-300
                    custom-scale hover:cursor-pointer hover:border-white hover:border-2"
        onClick={handleImageClick} // Open modal on click
      >
        <img
          className="w-full h-full object-cover object-[20%_40%] rounded-md"
          src={props.url}
          alt="img"
        />
      </div>

      {/* Title and Description */}
      <div className="hidden">
        <h3 className="text-left text-2xl mx-3">{props.title}</h3>
        <p className="text-justify mx-3">{props.description}</p>
        <audio className="px-2 my-2 w-full" controls></audio>
      </div>

      {/* Modal */}
      {isModalOpen && (
        <div
          className="fixed bottom-0 left-0 w-full h-full bg-black bg-opacity-60 backdrop-blur-md flex items-center justify-center z-50"
          onClick={handleCloseModal} // Close modal on clicking outside
        >
          <div
            className="bg-white w-[1100px] h-[670px] rounded-xl flex flex-row items-stretch"
            onClick={(e) => e.stopPropagation()} // Prevent closing when clicking inside the modal
          >
            {/* Black Section */}
            <div className={`bg-black basis-2/3 h-full rounded-l-xl border-2 border-black  ${divClass} justify-center`}>
              <img className={`big-img ${imageClass}`} src={props.url} alt="modal-img" />
            </div>

            {/* Red Section */}
            <div className="bg-white basis-1/3 h-full rounded-r-xl "></div>
          </div>
        </div>
      )}
    </div>
  );
}

export default ImageCard;
